(78, 1, '2015-02-23 10:38:41', '2015-02-23 10:38:41', '<h4>Backup MYSQL tables in sql files (php code)</h4>\n<h1><a href="http://localhost/blog2/wp-content/uploads/2015/02/PHP_Logo.png"><img class="alignnone  wp-image-80" src="http://localhost/blog2/wp-content/uploads/2015/02/PHP_Logo-300x145.png" alt="PHP_Logo" width="995" height="481" /></a></h1>\ni wrote this function to backup mysql tables in sql files.\n\nhere is how to use the function:\n// backup_tables(db_host,db_user,db_pass,db_name, [array or *] $tables_array ,[ boolean] $all_in_one);\n\ndb_host : localhost\ndb_user : root\ndb_pass : ''''\ndb_name : db_name\n[optional - default: *] $tables_array : can be * for all tables or an array of selected tables to back up...\n[optional - default: false] $all_in_one : backup all tables in one sql file or each table in a sql file.\n\nEx:--    backup_tables(''localhost'',''root'','''',''blog'',''*'',false);\n<h3>Here is the function :</h3>\n<pre>function backup_tables($host,$user,$pass,$name,$tables = ''*'',$each_table_alone = true)\n{\n    \n    $link = mysql_connect($host,$user,$pass);\n    mysql_select_db($name,$link);\n    \n    //get all of the tables\n    if($tables == ''*''){\n        $tables = array();\n        $result = mysql_query(''SHOW TABLES'');\n        while($row = mysql_fetch_row($result))\n        {\n            $tables[] = $row[0];\n        }\n    }\n    else{\n        $tables = is_array($tables) ? $tables : explode('','',$tables);\n    }\n    \n    $return = '''';\n    $folder = create_folder(''db-backup-''.date(''Y-M-d h-i'',time()));\n    //cycle through\n    foreach($tables as $table)\n    {\n        $result = mysql_query(''SELECT * FROM ''.$table);\n        $num_fields = mysql_num_fields($result);\n        \n        $return.= ''DROP TABLE ''.$table.'';'';\n        $row2 = mysql_fetch_row(mysql_query(''SHOW CREATE TABLE ''.$table));\n        $return.= "\n\n".$row2[1].";\n\n";\n        \n        for ($i = 0; $i &lt; $num_fields; $i++) \n        {\n            while($row = mysql_fetch_row($result))\n            {\n                $return.= ''INSERT INTO ''.$table.'' VALUES('';\n                for($j=0; $j&lt;$num_fields; $j++) \n                {\n                    $row[$j] = addslashes($row[$j]);\n                    $row[$j] = ereg_replace("\n","\\n",$row[$j]);\n                    if (isset($row[$j])) { $return.= ''"''.$row[$j].''"'' ; } else { $return.= ''""''; }\n                    if ($j&lt;($num_fields-1)) { $return.= '',''; }\n                }\n                $return.= ");\n";\n            }\n        }\n        $return.="\n\n\n";\n\n        if($each_table_alone){\n            $handle = fopen($folder.''/''.$table.''.sql'',''w+'');\n            fwrite($handle,$return);\n            fclose($handle);\n        }\n    }\n    \n    //save file\n    if(!$each_table_alone){\n        $handle = fopen($folder.''/selected_tables.sql'',''w+'');\n        fwrite($handle,$return);\n        fclose($handle);\n    }\n\n}\n\n\nfunction create_folder($path){\n    if (!file_exists($path)) {\n        mkdir($path, 0777, true);\n    }\n    return $path;\n}</pre>', 'Backup MYSQL tables (in php)', '', 'publish', 'open', 'open', '', 'backup-all-mysql-tables-in-php-easy-way', '', '', '2015-02-23 10:46:58', '2015-02-23 10:46:58', '', 0, 'http://localhost/blog2/?p=78', 0, 'post', '', 0),
